function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   alert(newValue);
	var universityId = newValue;
	var objCourseChoices = new GlideAjax('GetCoursesChoices');
	objCourseChoices.addParam('sysparm_name', 'getCourses');
	objCourseChoices.addParam('sysparm_UID', g_form.getValue('ucs_university'));
	alert(g_form.getValue('ucs_university'));
	objCourseChoices.getXMLAnswer(populateChoices);

	function populateChoices(response) {
		var courseChoices = response;
		for(var i = 0; i < courseChoices.length; i++) {
			g_form.addOption('ucs_course',courseChoices[0],courseChoices[0]);
		}
		alert(response);
	}
}
