sys_script_include.script:
var AssignmentFunctions = Class.create();
AssignmentFunctions.prototype = {
	getCourses: function(university) {
		gs.info("Praveen - Inside function");
		var objGlideRecord = new GlideRecord('x_526643_assignmen_university_courses');
		objGlideRecord.addQuery('uc_name', university);
		objGlideRecord.query();
		var courses = [];
		while(objGlideRecord.next()) {
			courses.push(objGlideRecord.getValue('uc_course'));
		}
		return courses.toString();
	},
    type: 'AssignmentFunctions'
};
