sys_security_acl.script:
answer = getValue();

function getValue() {

    var test_admin = gs.hasRole('x_526643_assignmen.test_admin');

    var test_user = gs.hasRole('x_526643_assignmen.test_user');

    if (test_admin) {
        return true;
    } else if (test_user && (current.name == gs.getUserID())) {
        return true;
    } else {
        return false;
    }

}
